con:testCase.con:setupScript:
import com.eviware.soapui.impl.wsdl.teststeps.*
import com.eviware.soapui.support.types.*;

StringToStringMap headers = new StringToStringMap();
String userPass = testCase.testSuite.project.getPropertyValue("meveoUsername")+":"+testCase.testSuite.project.getPropertyValue("meveoPassword");
 log.info("userPass="+userPass);
headers.put("Authorization","Basic "+userPass.getBytes().encodeBase64());
for(testSuiteItem in  testCase.testSuite.project.getTestSuiteList()){
	for( testCaseItem in testSuiteItem.getTestCaseList() ) {
	    log.info("Setting basic auth for all WSDL test requests in test case ["+testCaseItem.getLabel()+"]")
	    for( testStep in testCaseItem.getTestStepList() ) {
	        testStep.getTestRequest().setPreemptive(true);
	        testStep.getTestRequest().setRequestHeaders(headers);
	    }
	}
}

con:config.script:
// create groovyUtils and XmlHolder for response of Request 1 request
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder( "execute#Response" )
def prj = testRunner.testCase.testSuite.project.workspace.getProjectByName(testRunner.testCase.testSuite.project.name)
def tSuite = prj.testSuites['#1452 - [JOB]Create a synchronous WS that execute a job and return the report']
//.testCases['MediationJob']
def testCaseProperty = tSuite.getPropertyValue( "jobInstance" )
log.info (testCaseProperty)

for( item in holder.getNodeValues( "//message" )) {
	//log.info ("job instance : $item")
	tSuite.setPropertyValue( "jobInstance",  "$item")
	testCaseProperty = tSuite.getPropertyValue( "jobInstance" )
	log.info ("job instance updated : " + testCaseProperty)
}

